using Microsoft.Office.Interop.Word;\n\nclass Word2003Converter\n{\n    public static void ConvertToOpenXml(string filePath)\n    {\n        Application wordApplication = new Application();\n        Document wordDocument = wordApplication.Documents.Open(filePath);\n        wordDocument.SaveAs2(filePath + ".docx", WdSaveFormat.wdFormatDocumentDefault);\n        wordApplication.Quit();\n    }\n}