using Google.Apis.Analytics.v3;\nusing Google.Apis.Auth.OAuth2;\nusing Google.Apis.Services;\n\n// Create a new instance of the analytics service\nvar analyticsService = new AnalyticsService(new BaseClientService.Initializer()\n{\n    HttpClientInitializer = new ServiceAccountCredential(\n        new ServiceAccountCredential.Initializer("your-email@gserviceaccount.com")\n        {\n            Scopes = new[] { AnalyticsService.Scope.Analytics }\n        }.FromPrivateKeyFile("path-to-private-key", "notasecret")),\n    ApplicationName = "Your Application Name"\n});\n\n// Get the data from Google Analytics\nvar request = analyticsService.Data.Ga.Get("ga:your-profile-id", "2010-02-01", "2010-02-28", "ga:visits");\nrequest.Dimensions = "ga:source,ga:medium";\nrequest.Sort = "-ga:visits";\nrequest.MaxResults = 25;\n\nvar data = request.Execute();\nforeach (var row in data.Rows)\n{\n    Console.WriteLine(string.Join(", ", row));\n}