using System;\nusing System.Collections.Generic;\nusing System.Runtime.InteropServices;\n\npublic class ComObjectTracker\n{\n    private Dictionary<object, string> _comObjects = new Dictionary<object, string>();\n\n    public void TrackComObject(object comObject, string description)\n    {\n        _comObjects.Add(comObject, description);\n    }\n\n    public void ReleaseComObject(object comObject)\n    {\n        if (_comObjects.ContainsKey(comObject))\n        {\n            _comObjects.Remove(comObject);\n            Marshal.ReleaseComObject(comObject);\n        }\n    }\n\n    public void DumpUnreleasedComObjects()\n    {\n        Console.WriteLine("Unreleased COM Objects:");\n        foreach (var pair in _comObjects)\n        {\n            Console.WriteLine($"  {pair.Value} ({pair.Key.GetType().Name})");\n        }\n    }\n}