using System.DirectoryServices.AccountManagement;\n\npublic bool ValidateExpiredPassword(string username, string password)\n{\n    PrincipalContext context = new PrincipalContext(ContextType.Domain, "yourdomain.com");\n    try\n    {\n        // Try to authenticate the user with the provided password.\n        bool authenticated = context.ValidateCredentials(username, password);\n\n        // If the authentication is successful, check if the password is expired.\n        UserPrincipal user = UserPrincipal.FindByIdentity(context, IdentityType.SamAccountName, username);\n        return user.LastPasswordSet.Value.AddMinutes(user.AccountLockoutDuration).CompareTo(DateTime.Now) < 0;\n    }\n    catch (PrincipalOperationException ex)\n    {\n        // If the authentication fails, the password is not valid.\n        return false;\n    }\n}