using System.DirectoryServices;\n\npublic bool ValidateExpiredPassword(string username, string password)\n{\n    DirectoryEntry entry = new DirectoryEntry("LDAP://yourdomain.com", username, password);\n    try\n    {\n        // Try to bind to the directory entry with the provided credentials.\n        object nativeObject = entry.NativeObject;\n\n        // If the bind is successful, check if the password is expired.\n        DirectorySearcher searcher = new DirectorySearcher(entry);\n        searcher.Filter = "(objectClass=user)";\n        searcher.PropertiesToLoad.Add("userAccountControl");\n        SearchResult result = searcher.FindOne();\n\n        // The password is expired if the UserAccountControl attribute contains the UF_PASSWORD_EXPIRED flag.\n        int userAccountControl = Convert.ToInt32(result.Properties["userAccountControl"][0]);\n        const int UF_PASSWORD_EXPIRED = 0x800000;\n        return (userAccountControl & UF_PASSWORD_EXPIRED) == UF_PASSWORD_EXPIRED;\n    }\n    catch (DirectoryServicesCOMException ex)\n    {\n        // If the bind fails, the password is not valid.\n        return false;\n    }\n}