SqlCommand cmd = new SqlCommand("select * from testing for xml auto", connection);\nusing (SqlDataReader reader = cmd.ExecuteReader())\n{\n    while (reader.Read())\n    {\n        string xml = (string)reader[0];\n        using (StringReader stringReader = new StringReader(xml))\n        {\n            using (XmlReader xmlReader = XmlReader.Create(stringReader, new XmlReaderSettings { Normalization = false }))\n            {\n                while (xmlReader.Read())\n                {\n                    string s = xmlReader.ReadOuterXml();\n                    records.Add(s);\n                }\n            }\n        }\n    }\n}