using System;\nusing System.Windows.Forms;\n\npublic class Session Timeout\n{\n    private Timer _timer;\n    private int _timeoutInterval = 30; // 30 minutes\n\n    public SessionTimeout()\n    {\n        _timer = new Timer();\n        _timer.Interval = _timeoutInterval * 60 * 1000; // convert minutes to milliseconds\n        _timer.Tick += new EventHandler(OnTimerTick);\n        _timer.Start();\n    }\n\n    private void OnTimerTick(object sender, EventArgs e)\n    {\n        // Session has timed out, prompt for login/password or log out\n        MessageBox.Show("Session has timed out. Please login again.");\n        // Add code to log out or prompt for login here\n    }\n\n    public void ResetTimer()\n    {\n        _timer.Stop();\n        _timer.Start();\n    }\n}