public class OrdersMap : ClassMap<Orders>\n{\n    public OrdersMap()\n    {\n        Id(x => x.OrderID);\n        Map(x => x.OrderDate);\n        Map(x => x.RequiredDate);\n        Map(x => x.ShippedDate);\n        References(x => x.Employee);\n        HasManyToMany(x => x.Products)\n            .Cascade.All()\n            .WithTableName("Order Details")\n            .WithParentKeyColumn("OrderID")\n            .WithChildKeyColumn("ProductID");\n    }\n}\n\npublic class ProductsMap : ClassMap<Products>\n{\n    public ProductsMap()\n    {\n        Id(x => x.ProductID);\n        Map(x => x.ProductName);\n        Map(x => x.UnitPrice);\n        HasManyToMany(x => x.Orders)\n            .Cascade.All()\n            .Inverse()\n            .WithTableName("Order Details")\n            .WithParentKeyColumn("ProductID")\n            .WithChildKeyColumn("OrderID");\n    }\n}