protected void AddPhotoToDatabase()\n{\n    byte[] OriginalPhoto = GetImage();\n    byte[] Thumbnail = GenerateThumbnail();\n    string Title = FileUpload1.FileName.ToString();\n\n    string sql = "INSERT INTO [ProfileGallery] ([UserId], [Title], [OriginalImage], [ThumbImage]) VALUES (@UserId, @Title, @OriginalImage, @ThumbImage)";\n\n    string strCon = System.Web.Configuration.WebConfigurationManager.ConnectionStrings["SocialSiteConnectionString"].ConnectionString;\n    SqlConnection conn = new SqlConnection(strCon);\n    SqlCommand comm = new SqlCommand(sql, conn);\n\n    comm.Parameters.AddWithValue("@UserId", User.Identity.Name);\n    comm.Parameters.AddWithValue("@Title", Title);\n    comm.Parameters.AddWithValue("@OriginalImage", OriginalPhoto);\n    comm.Parameters.AddWithValue("@ThumbImage", Thumbnail);\n\n    conn.Open();\n    comm.ExecuteNonQuery();\n    conn.Close();\n}