public partial class MainForm : Form\n{\n    private MyUserControl _myUserControl;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        _myUserControl = new MyUserControl();\n        _myUserControl.ValidationError += UserControl_ValidationError;\n    }\n\n    private void UserControl_ValidationError(object sender, ValidationErrorEventArgs e)\n    {\n        // Display error message on status bar\n        statusBar.Text = e.ErrorMessage;\n    }\n}