using System.Data;\nusing System.Data.SqlClient;\n\n// Connect to the database\nstring connectionString = "Server=myServer;Database=myDatabase;User Id=myUser;Password=myPassword;";\nSqlConnection connection = new SqlConnection(connectionString);\n\n// Create a SqlDataAdapter and SqlCommandBuilder\nSqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM myTable", connection);\nSqlCommandBuilder builder = new SqlCommandBuilder(adapter);\n\n// Generate the strongly-typed dataset\nDataSet dataset = new DataSet();\nadapter.Fill(dataset);\n\n// Generate the properties\nforeach (DataColumn column in dataset.Tables[0].Columns)\n{\n    Console.WriteLine(string.Format("public {0} {1} {{ get; set; }}", column.DataType.Name, column.ColumnName));\n}