using System;\nusing System.Diagnostics;\n\npublic class CiscoVpnClientChecker\n{\n    public bool IsVpnClientConnected()\n    {\n        // Check if the VPN client process is running\n        var processes = Process.GetProcessesByName("vpnclient");\n        if (processes.Length == 0)\n        {\n            return false;\n        }\n\n        // Check if the VPN client is connected\n        var process = processes[0];\n        var vpnClientExe = process.MainModule.FileName;\n        var args = vpnClientExe + " stat";\n        var output = RunCommand(args);\n\n        // Parse the output to check if the VPN client is connected\n        return output.Contains("State: Connected");\n    }\n\n    private string RunCommand(string args)\n    {\n        var process = new Process\n        {\n            StartInfo = new ProcessStartInfo\n            {\n                FileName = "cmd.exe",\n                Arguments = "/c " + args,\n                RedirectStandardOutput = true,\n                UseShellExecute = false,\n                CreateNoWindow = true\n            }\n        };\n        process.Start();\n        var output = process.StandardOutput.ReadToEnd();\n        process.WaitForExit();\n        return output;\n    }\n}