using System.Text.RegularExpressions;\n\n// Assuming 'input' is the user-provided text\nstring input = "Check out this link: https://example.com";\n\n// Use a regular expression to find all URLs in the input\nstring urlPattern = @"https?://\S+";\nMatchCollection matches = Regex.Matches(input, urlPattern);\n\n// Replace each URL with an anchor tag that opens in a new window\nforeach (Match match in matches)\n{\n    string url = match.Value;\n    input = input.Replace(url, $"<a href=\"{url}\" target=\"_blank\">{url}</a>");\n}