public class MyService : IMyService\n{\n    public Customer GetById(string id)\n    {\n        // ...\n\n        XslStylesheetAttribute attribute = (XslStylesheetAttribute)GetType().GetMethod("GetById").GetCustomAttributes(typeof(XslStylesheetAttribute), false).FirstOrDefault();\n\n        if (attribute != null)\n        {\n            XmlWriterSettings settings = new XmlWriterSettings();\n            settings.OmitXmlDeclaration = false;\n            settings.ProcessingInstructions.Add("xml-stylesheet", $"type='text/xsl' href='{attribute.Href}'");\n\n            using (XmlWriter writer = XmlWriter.Create(Response.OutputStream, settings))\n            {\n                XmlSerializer serializer = new XmlSerializer(typeof(Customer));\n                serializer.Serialize(writer, customer);\n            }\n        }\n\n        return customer;\n    }\n}