[ApiController]\n[Route("api/[controller]")]\npublic class ReportController : ControllerBase\n{\n    private readonly IReportServer _reportServer;\n\n    public ReportController(IReportServer reportServer)\n    {\n        _reportServer = reportServer;\n    }\n\n    [HttpGet]\n    public IActionResult GetReport()\n    {\n        var report = new Report();\n        report.ReportPath = "/MyReport";\n        report.ReportServerUrl = "http://myreportserver/reportserver";\n        var bytes = _reportServer.RenderReport(report);\n        return File(bytes, "application/pdf", "MyReport.pdf");\n    }\n}