public class WsdlExportExtension : IWsdlExportExtension\n{\n    public void ExportContract(WsdlExporter exporter, WsdlContractConversionContext context)\n    {\n        if (context.ContractType == typeof(IServerService))\n        {\n            var clientContract = typeof(IClientService);\n            var clientContractDescription = ContractDescription.GetContract(clientContract);\n\n            exporter.ExportContract(clientContractDescription);\n        }\n    }\n\n    public void ExportEndpoint(WsdlExporter exporter, WsdlEndpointConversionContext context)\n    {\n        // Not used in this example\n    }\n}