using System;\n\nclass TableDrawer\n{\n    public static void DrawTable(string[] columns, int[] widths, string[][] data)\n    {\n        // Draw header\n        Console.WriteLine(new string('-', widths.Sum() + columns.Length + 1));\n        for (int i = 0; i < columns.Length; i++)\n        {\n            Console.Write($"| {columns[i].PadRight(widths[i])} ");\n        }\n        Console.WriteLine("|");\n        Console.WriteLine(new string('-', widths.Sum() + columns.Length + 1));\n\n        // Draw data rows\n        foreach (var row in data)\n        {\n            for (int i = 0; i < row.Length; i++)\n            {\n                Console.Write($"| {row[i].PadRight(widths[i])} ");\n            }\n            Console.WriteLine("|");\n        }\n\n        // Draw footer\n        Console.WriteLine(new string('-', widths.Sum() + columns.Length + 1));\n    }\n}