[WebMethod]\npublic string GetVendors(string Database)\n{\n    // Validate user input\n    if (string.IsNullOrEmpty(Database) || !Regex.IsMatch(Database, @"^[a-zA-Z0-9]+$"))\n    {\n        throw new ArgumentException("Invalid database name");\n    }\n\n    SqlConnection sqlConn = new SqlConnection();\n    sqlConn.ConnectionString = GetConnString(Database);\n\n    // Build query\n    string strSQL = @"SELECT [No_] AS [VendorNo], LTRIM([Name]) AS [VendorName]\n                      FROM [********_$Vendor]\n                      WHERE LEN(RTRIM([Name])) > 0 AND [Vendor Posting Group] = 'VEND'\n                      ORDER BY LTRIM([Name]) ASC";\n\n    SqlDataAdapter da = new SqlDataAdapter(strSQL, sqlConn);\n    DataSet ds = new DataSet();\n    da.Fill(ds, "Vendors");\n\n    // Serialize the DataSet to a JSON string\n    string json = JsonConvert.SerializeObject(ds);\n\n    return json;\n}