using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\n// ...\n\nList<ReportClass> reports = new List<ReportClass>();\nreports.Add(new WeeklyReport1());\nreports.Add(new WeeklyReport2());\nreports.Add(new WeeklyReport3());\n// ...\n\nusing (FileStream fs = new FileStream(@"c:\reports\combined.pdf", FileMode.Create))\n{\n    Document doc = new Document();\n    PdfCopy copy = new PdfCopy(doc, fs);\n    doc.Open();\n\n    foreach (ReportClass report in reports)\n    {\n        string pdfFile = @"c:\reports\" + report.ResourceName + ".pdf";\n        PdfReader reader = new PdfReader(pdfFile);\n        copy.AddDocument(reader);\n        reader.Close();\n    }\n\n    doc.Close();\n}