// In your code-behind file (e.g., report.aspx.cs)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    string startDate = Request.QueryString["StartDate"];\n    string endDate = Request.QueryString["EndDate"];\n\n    // Validate and sanitize the input parameters\n    DateTime startDateTime;\n    DateTime endDateTime;\n    if (DateTime.TryParse(startDate, out startDateTime) && DateTime.TryParse(endDate, out endDateTime))\n    {\n        // Construct the URL with validated parameters\n        string url = "/Reports/report.aspx?StartDate=" + startDateTime.ToString("yyyy-MM-dd") + "&EndDate=" + endDateTime.ToString("yyyy-MM-dd");\n        Response.Redirect(url);\n    }\n    else\n    {\n        // Handle invalid input parameters\n    }\n}