using System.Security;\nusing System.Security.Policy;\n\n// Create a new CodeGroup\nCodeGroup testGroup = new CodeGroup("test", new ZoneMembershipCondition(SecurityZone.MyComputer));\n\n// Set the permission set to Internet\ntestGroup.PermissionSet = new NamedPermissionSet("Internet");\n\n// Add the testGroup to the User policy level\nSecurityManager.UserPolicy.Level.AddCodeGroup(testGroup);\n\n// Change the permission set of All_Code to Nothing\nCodeGroup allCodeGroup = SecurityManager.UserPolicy.Level.GetCodeGroup("All_Code");\nallCodeGroup.PermissionSet = new NamedPermissionSet("Nothing");