using System;\nusing System.IO;\nusing DocumentFormat.OpenXml;\nusing DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Wordprocessing;\n\nclass EquationExtractor\n{\n    public static void ExtractEquationsAndImages(string filePath)\n    {\n        using (WordprocessingDocument wordDocument = WordprocessingDocument.Open(filePath, true))\n        {\n            MainDocumentPart mainPart = wordDocument.MainDocumentPart;\n\n            // Extract equations\n            foreach (MathParagraph mathParagraph in mainPart.Document.Body.Descendants<MathParagraph>())\n            {\n                Console.WriteLine("Equation:");\n                Console.WriteLine(mathParagraph.OuterXml);\n            }\n\n            // Extract images\n            foreach (ImagePart imagePart in mainPart.ImageParts)\n            {\n                using (Stream imageStream = imagePart.GetStream())\n                {\n                    using (FileStream fileStream = new FileStream("image" + imagePart.Uri.ToString().Split('/').Last(), FileMode.Create))\n                    {\n                        imageStream.CopyTo(fileStream);\n                    }\n                }\n            }\n        }\n    }\n}