// .NET server-side code\nusing System.Net;\nusing System.Web;\nusing HtmlAgilityPack;\n\npublic class ScrapingController : Controller\n{\n  public ActionResult Scrape(string url)\n  {\n    // Open the URL and scrape the data\n    var web = new HtmlWeb();\n    var doc = web.Load(url);\n    var data = doc.DocumentNode.SelectSingleNode("//div[@id='data']").InnerText;\n    \n    // Generate the iframe content\n    var iframeContent = "<html><body><div id='data'>" + data + "</div></body></html>";\n    \n    return Content(iframeContent, "text/html");\n  }\n}