using Quartz;\n\n// Create a new scheduler\nStdSchedulerFactory factory = new StdSchedulerFactory();\nIScheduler scheduler = factory.GetScheduler();\n\n// Schedule a job to run daily at 2am\nIJobDetail job = JobBuilder.Create<SendEmailsFromQueueJob>().Build();\nITrigger trigger = TriggerBuilder.Create().WithDailyTimeIntervalSchedule(x => x.OnEveryDay().StartingDailyAt(TimeOfDay.HourAndMinuteOfDay(2, 0))).Build();\nscheduler.ScheduleJob(job, trigger);