protected void Application_AuthenticateRequest(object sender, EventArgs e)\n{\n    var ticket = FormsAuthentication.Decrypt(Request.Cookies[FormsAuthentication.FormsCookieName].Value);\n    if (ticket != null && ticket.UserData == HttpContext.Current.Session.SessionID)\n    {\n        // Cookie is valid, proceed with authentication\n    }\n    else\n    {\n        // Cookie is invalid, handle accordingly\n    }\n}