private void OnButtonClick(object sender, RoutedEventArgs e)\n{\n    var muc = new MyUserControl();\n    dockPanel.Children.Add(muc);\n    muc.DataContext = new MyObject();\n\n    // Force validation to run after the control has been loaded\n    muc.Dispatcher.BeginInvoke((Action)(() =>\n    {\n        muc.GetBindingExpression(TextBox.TextProperty).UpdateSource();\n    }), DispatcherPriority.Loaded);\n}