using System.Net.Http;\nusing System.Xml.Serialization;\n\npublic class MyWebServiceClient\n{\n    private readonly HttpClient _httpClient;\n\n    public MyWebServiceClient(string baseAddress)\n    {\n        _httpClient = new HttpClient { BaseAddress = new Uri(baseAddress) };\n    }\n\n    public async Task<MyResponse> MyMethodAsync(MyRequest request)\n    {\n        var requestXml = SerializeRequest(request);\n        var response = await _httpClient.PostAsync("mywebservice", new StringContent(requestXml, Encoding.UTF8, "text/xml"));\n        var responseXml = await response.Content.ReadAsStringAsync();\n        return DeserializeResponse(responseXml);\n    }\n\n    private string SerializeRequest(MyRequest request)\n    {\n        var serializer = new XmlSerializer(typeof(MyRequest));\n        var writer = new StringWriter();\n        serializer.Serialize(writer, request);\n        return writer.ToString();\n    }\n\n    private MyResponse DeserializeResponse(string responseXml)\n    {\n        var serializer = new XmlSerializer(typeof(MyResponse));\n        var reader = new StringReader(responseXml);\n        return (MyResponse)serializer.Deserialize(reader);\n    }\n}