using System.IO;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Set the directory path and resource file path\n        string directoryPath = @"C:\YourFiles";\n        string resourceFilePath = @"C:\Resources.resx";\n\n        // Iterate through all files in the directory\n        foreach (string file in Directory.GetFiles(directoryPath))\n        {\n            // Add the file to the resource file\n            AddFileToResource(file, resourceFilePath);\n        }\n    }\n\n    static void AddFileToResource(string file, string resourceFilePath)\n    {\n        // Get the file name and extension\n        string fileName = Path.GetFileName(file);\n        string fileExtension = Path.GetExtension(file);\n\n        // Create a new resource file if it doesn't exist\n        if (!File.Exists(resourceFilePath))\n        {\n            using (StreamWriter writer = File.CreateText(resourceFilePath))\n            {\n                writer.WriteLine("<?xml version=\"1.0\" encoding=\"utf-8\"?>");\n                writer.WriteLine("<root>");\n            }\n        }\n\n        // Add the file to the resource file\n        using (StreamWriter writer = File.AppendText(resourceFilePath))\n        {\n            writer.WriteLine($"  <data name=\"{fileName}\">");\n            writer.WriteLine($"    <value>{file}</value>");\n            writer.WriteLine($"  </data>");\n        }\n    }\n}