using System.Security.Cryptography;\n\npublic class IntegrityCheck\n{\n    public static bool VerifyIntegrity(string filePath)\n    {\n        // Calculate the expected hash\n        byte[] expectedHash = CalculateHash(filePath);\n\n        // Read the file and calculate the actual hash\n        byte[] actualHash = CalculateHash(File.ReadAllBytes(filePath));\n\n        // Compare the hashes\n        return expectedHash.SequenceEqual(actualHash);\n    }\n\n    private static byte[] CalculateHash(byte[] data)\n    {\n        using (var hashAlgorithm = SHA256.Create())\n        {\n            return hashAlgorithm.ComputeHash(data);\n        }\n    }\n}