using System;\n\npublic class DebugToggle\n{\n    private readonly Func<bool> _isEnabled;\n\n    public DebugToggle(Func<bool> isEnabled)\n    {\n        _isEnabled = isEnabled;\n    }\n\n    public void DebugLog(string message)\n    {\n        if (_isEnabled())\n        {\n            Console.WriteLine(message);\n        }\n    }\n}\n\n// Usage:\nvar toggle = new DebugToggle(() => GetDebugToggleFromDatabase());\ntoggle.DebugLog("This will only be logged if debug toggle is enabled");