string dll = textbox1.Text;      // ex "ProjectA.dll"\nstring className = textbox2.Text;    // ex "A`2[enuT,enuJ]"\n\n// Load the assembly and get the type\nAssembly assembly = Assembly.LoadFrom(dll);\nType type = assembly.GetType(className);\n\n// Get the base type\nType baseType = type.BaseType;\n\n// Make the generic type\nType genericType = baseType.MakeGenericType(typeof(enuT), typeof(enuJ));\n\n// Create an instance of the generic type\nobject obj = Activator.CreateInstance(genericType);\n\n// Get the public methods of the base class\nMethodInfo[] methods = genericType.GetMethods(BindingFlags.Public | BindingFlags.Instance);\n\n// Invoke a method\nMethodInfo method = methods.FirstOrDefault(m => m.Name == "item");\nobject result = method.Invoke(obj, null);