using System.Security.AccessControl;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\n// ...\n\nX509Certificate2 cert = new X509Certificate2("test.pfx", "password");\n\n// Get the private key\nRSACryptoServiceProvider privateKey = (RSACryptoServiceProvider)cert.PrivateKey;\n\n// Get the file path of the private key\nstring privateKeyPath = privateKey.GetPrivateKeyFile();\n\n// Create a new ACL\nFileSecurity acl = new FileSecurity(privateKeyPath, AccessControlSections.Access);\n\n// Create a rule for NetworkService\nFileSystemAccessRule rule = new FileSystemAccessRule("NT AUTHORITY\\NetworkService", FileSystemRights.Read, AccessControlType.Allow);\n\n// Add the rule to the ACL\nacl.AddAccessRule(rule);\n\n// Set the ACL on the private key file\nFile.SetAccessControl(privateKeyPath, acl);