using System.Diagnostics;\n\npublic class PerformanceMonitor\n{\n    private readonly string _categoryName;\n    private readonly string _counterName;\n\n    public PerformanceMonitor(string categoryName, string counterName)\n    {\n        _categoryName = categoryName;\n        _counterName = counterName;\n    }\n\n    public void CreateCounter()\n    {\n        if (!PerformanceCounterCategory.Exists(_categoryName))\n        {\n            PerformanceCounterCategory.Create(_categoryName, _categoryName, PerformanceCounterCategoryType.MultiInstance);\n        }\n\n        if (!PerformanceCounter.Exists(_categoryName, _counterName))\n        {\n            PerformanceCounter.Create(_categoryName, _counterName, PerformanceCounterType.NumberOfItems32);\n        }\n    }\n\n    public void IncrementCounter()\n    {\n        using (var counter = new PerformanceCounter(_categoryName, _counterName, false))\n        {\n            counter.Increment();\n        }\n    }\n}