[TestMethod]\npublic void TestEventHandler()\n{\n    // Create a manual reset event\n    ManualResetEvent waitEvent = new ManualResetEvent(false);\n\n    // Create an instance of your class that raises the event\n    MyEventRaisingClass instance = new MyEventRaisingClass();\n\n    // Subscribe to the event\n    instance.MyEvent += (sender, args) =>\n    {\n        // Set the event when it's triggered\n        waitEvent.Set();\n    };\n\n    // Call the method that raises the event\n    instance.RaiseEvent();\n\n    // Wait for the event to be triggered\n    if (!waitEvent.WaitOne(TimeSpan.FromSeconds(10)))\n    {\n        Assert.Fail("Event was not triggered within the timeout period");\n    }\n}