protected void Page_Load(object sender, EventArgs e)\n{\n    string keyword = Request.QueryString["keyword"];\n\n    XPathNavigator navigator = RSSFeedDataSource.GetXmlDocument().CreateNavigator();\n    XPathExpression expression = navigator.Compile("/rss/channel/item[contains(title, $keyword)]");\n    expression.SetParameter("keyword", keyword);\n\n    RSSFeedDataSource.XPath = expression;\n}