[TestMethod]\npublic async Task TestEventHandler()\n{\n    // Create a task completion source\n    TaskCompletionSource<bool> taskCompletionSource = new TaskCompletionSource<bool>();\n\n    // Create an instance of your class that raises the event\n    MyEventRaisingClass instance = new MyEventRaisingClass();\n\n    // Subscribe to the event\n    instance.MyEvent += (sender, args) =>\n    {\n        // Set the result when it's triggered\n        taskCompletionSource.SetResult(true);\n    };\n\n    // Call the method that raises the event\n    instance.RaiseEvent();\n\n    // Wait for the event to be triggered\n    if (!await taskCompletionSource.Task.TimeoutAfter(TimeSpan.FromSeconds(10)))\n    {\n        Assert.Fail("Event was not triggered within the timeout period");\n    }\n}