// RouteConfig.cs\npublic static class RouteConfig\n{\n    public static void RegisterRoutes(RouteCollection routes)\n    {\n        routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n        // existing routes ...\n\n        routes.MapRoute(\n            name: "MemberProfile",\n            template: "members/{username}",\n            defaults: new { controller = "Members", action = "Profile" }\n        );\n\n        // default route ...\n    }\n}