// In your web application\nusing System.Security.Principal;\n\n// Get the current Windows identity\nWindowsIdentity identity = (WindowsIdentity)HttpContext.Current.User.Identity;\n\n// Create a new WindowsIdentity token\nWindowsIdentityToken token = new WindowsIdentityToken(identity);\n\n// Serialize the token to a byte array\nbyte[] tokenBytes = token.Serialize();\n\n// Pass the token bytes over the remoting call\nYourService service = new YourService();\nservice.DoSomething(tokenBytes);