// Store document metadata in database\npublic class DocumentMetadata {\n    public int Id { get; set; }\n    public string FileName { get; set; }\n    public string FileExtension { get; set; }\n    public string StorageLocation { get; set; } // e.g. NAS or SAN path\n}\n\n// Store document in file system\npublic void StoreDocument(DocumentMetadata metadata, byte[] fileContent) {\n    string filePath = Path.Combine(metadata.StorageLocation, metadata.FileName);\n    File.WriteAllBytes(filePath, fileContent);\n}\n\n// Retrieve document from file system\npublic byte[] RetrieveDocument(DocumentMetadata metadata) {\n    string filePath = Path.Combine(metadata.StorageLocation, metadata.FileName);\n    return File.ReadAllBytes(filePath);\n}