using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\n// assuming the PDF file is saved at @"C:\path\to\report.pdf"\nstring filePath = @"C:\path\to\report.pdf";\nstring connectionString = "Your SQL Server connection string";\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    using (SqlCommand command = new SqlCommand("INSERT INTO YourTable (PdfColumn) VALUES (@PdfData)", connection))\n    {\n        byte[] pdfData = File.ReadAllBytes(filePath);\n        command.Parameters.AddWithValue("@PdfData", pdfData);\n\n        command.ExecuteNonQuery();\n    }\n}