using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\nstring connectionString = "Your SQL Server connection string";\nint id = 1; // assuming the ID of the row you want to retrieve\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    using (SqlCommand command = new SqlCommand("SELECT PdfColumn FROM YourTable WHERE Id = @Id", connection))\n    {\n        command.Parameters.AddWithValue("@Id", id);\n\n        using (SqlDataReader reader = command.ExecuteReader())\n        {\n            if (reader.Read())\n            {\n                byte[] pdfData = (byte[])reader["PdfColumn"];\n                using (FileStream fs = new FileStream(@"C:\path\to\output.pdf", FileMode.Create))\n                {\n                    fs.Write(pdfData, 0, pdfData.Length);\n                }\n            }\n        }\n    }\n}