// Person.cs\npublic class Person\n{\n    public string Name { get; set; }\n    public IList<PhoneNumber> PhoneNumbers { get; set; }\n    public IList<Address> Addresses { get; set; }\n}\n\n// PhoneNumber.cs\npublic class PhoneNumber\n{\n    public string Number { get; set; }\n}\n\n// Address.cs\npublic class Address\n{\n    public string Street { get; set; }\n    public string City { get; set; }\n}\n\n// PersonController.cs\npublic class PersonController : Controller\n{\n    [HttpGet]\n    public ActionResult Create()\n    {\n        var person = new Person();\n        return View(person);\n    }\n\n    [HttpPost]\n    public ActionResult Create(Person person)\n    {\n        if (ModelState.IsValid)\n        {\n            // Save person to database\n        }\n        return View(person);\n    }\n}\n\n// Create.cshtml\n@model Person\n\n@using (Html.BeginForm())\n{\n    @Html.EditorFor(m => m.Name)\n\n    <h2>Phone Numbers</h2>\n    <ul id="phone-numbers">\n        @Html.EditorFor(m => m.PhoneNumbers)\n    </ul>\n    <button id="add-phone-number">Add Phone Number</button>\n\n    <h2>Addresses</h2>\n    <ul id="addresses">\n        @Html.EditorFor(m => m.Addresses)\n    </ul>\n    <button id="add-address">Add Address</button>\n\n    <input type="submit" value="Save" />\n}\n\n// EditorTemplates/PhoneNumber.cshtml\n@model PhoneNumber\n\n<li>\n    @Html.EditorFor(m => m.Number)\n    <button class="remove-phone-number">Remove</button>\n</li>\n\n// EditorTemplates/Address.cshtml\n@model Address\n\n<li>\n    @Html.EditorFor(m => m.Street)\n    @Html.EditorFor(m => m.City)\n    <button class="remove-address">Remove</button>\n</li>\n\n// JavaScript\n$(document).ready(function () {\n    $('#add-phone-number').click(function () {\n        var newPhoneNumber = $('<li>@Html.EditorFor(m => m.PhoneNumbers[0])</li>');\n        $('#phone-numbers').append(newPhoneNumber);\n    });\n\n    $('#add-address').click(function () {\n        var newAddress = $('<li>@Html.EditorFor(m => m.Addresses[0])</li>');\n        $('#addresses').append(newAddress);\n    });\n\n    $('.remove-phone-number').live('click', function () {\n        $(this).closest('li').remove();\n    });\n\n    $('.remove-address').live('click', function () {\n        $(this).closest('li').remove();\n    });\n});