using System;\nusing System.Threading;\nusing System.Runtime.InteropServices;\n\n// Wrapper assembly\npublic class ComWrapper\n{\n    private readonly object _lock = new object();\n    private readonly ComDll _comDll;\n\n    public ComWrapper()\n    {\n        _comDll = new ComDll();\n    }\n\n    public string GetData()\n    {\n        lock (_lock)\n        {\n            // Call COM DLL method\n            return _comDll.GetData();\n        }\n    }\n}\n\n// COM DLL interface\n[InterfaceType(ComInterfaceType.InterfaceIsDual)]\npublic interface IComDll\n{\n    string GetData();\n}\n\n// COM DLL implementation\npublic class ComDll : IComDll\n{\n    public string GetData()\n    {\n        // Simulate database query\n        Thread.Sleep(100);\n        return "Data from database";\n    }\n}