using (var dc = new MyDataContext())\n{\n    dc.Log = Console.Out; // log queries to console\n\n    var myQuery = from c in dc.Customers select c;\n    var filter1 = from c in myQuery where c.ID > 2 select c;\n    myQuery = filter1;\n    var filter2 = from c in myQuery where c.Name.Contains("r") select c;\n    myQuery = filter2;\n\n    foreach (var customer in myQuery)\n    {\n        Console.WriteLine(customer.Name);\n    }\n}