using (var dbContext = new YourDbContext()) // Replace YourDbContext with the actual name of your DbContext\n{\n    // Assuming fruit is the Fruit object you have from a previous LINQ to Entities call\n    fruit.FruitName = "New Fruit Name";\n    fruit.FruitStatusID = newStatusID; // newStatusID is the ID of the new status you want to assign\n\n    dbContext.Entry(fruit).State = EntityState.Modified; // Mark the entity as modified\n    dbContext.SaveChanges();\n}