public partial class HomePage : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Retrieve all news items inserted for today\n        DataTable newsItems = News.GetNewsItemsForToday();\n\n        // Populate headlines section\n        DataView headlinesView = new DataView(newsItems);\n        headlinesView.RowFilter = "NewsType = 'Headline'";\n        headlinesRepeater.DataSource = headlinesView;\n        headlinesRepeater.DataBind();\n\n        // Populate spot news section\n        DataView spotNewsView = new DataView(newsItems);\n        spotNewsView.RowFilter = "NewsType = 'Spot'";\n        spotNewsRepeater.DataSource = spotNewsView;\n        spotNewsRepeater.DataBind();\n\n        // ...\n    }\n}