public class VisibilityAwareValidationRule : ExceptionValidationRule\n{\n    public VisibilityAwareValidationRule()\n    {\n        // Listen to panel's Visibility property\n        var panel = (FrameworkElement)Application.Current.MainWindow.FindName("panel1");\n        panel.VisibilityChanged += Panel_VisibilityChanged;\n    }\n\n    private void Panel_VisibilityChanged(object sender, DependencyPropertyChangedEventArgs e)\n    {\n        if ((Visibility)e.NewValue == Visibility.Collapsed)\n        {\n            // Reset validation error\n            var bindingExpression = DataBoundTextBox.GetBindingExpression(TextBox.TextProperty);\n            bindingExpression.UpdateTarget();\n        }\n    }\n}