using RazorEngine;\nusing RazorEngine.Templating;\n\npublic class HtmlGenerator\n{\n    public static void GenerateHtml(string htmlPath, string template, object customDataObject)\n    {\n        var templateService = new TemplateService();\n        var templateResult = templateService.Parse(template, customDataObject, null, null);\n\n        using (var writer = new StreamWriter(htmlPath))\n        {\n            writer.Write(templateResult);\n        }\n    }\n}