public static class DataFormSubject\n{\n    private static ObservableList<DataForm> _dataForms = new ObservableList<DataForm>();\n\n    public static event NotifyCollectionChangedEventHandler DataFormsUpdatedEvent\n    {\n        add { _dataForms.CollectionChanged += value; }\n        remove { _dataForms.CollectionChanged -= value; }\n    }\n\n    public static void AddDataForm(DataForm df)\n    {\n        _dataForms.Add(df);\n    }\n\n    public static void RemoveDataForm(DataForm df)\n    {\n        _dataForms.Remove(df);\n    }\n\n    public static ObservableList<DataForm> DataForms => _dataForms;\n}