using QBFC13Lib;\n\n// Initialize the QuickBooks session\nQBSessionManager sessionManager = new QBSessionManager();\nsessionManager.OpenConnection("", "YourCompanyFile.qbw");\n\n// Create a request to retrieve customer data\nQBRequest request = sessionManager.CreateMsgSetRequest("US", 13, 0);\nICustomerQuery customerQuery = request.AppendCustomerQueryRq();\ncustomerQuery.ORCustomerListQuery.NameList.Add("YourCustomerName");\n\n// Send the request and retrieve the response\nQBResponse response = sessionManager.DoRequests(request);\nICustomerRetList customerList = (ICustomerRetList)response.Detail;\n\n// Process the customer data\nforeach (ICustomerRet customer in customerList)\n{\n    Console.WriteLine(customer.Name);\n}